import React, { useState } from 'react';
import EpisodeDetailsForm from './EpisodeDetailsForm'; // Assurez-vous que le chemin d'importation est correct

function App() {
  const [file, setFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [transcription, setTranscription] = useState('');
  const [currentStep, setCurrentStep] = useState('upload'); // 'upload', 'details', 'generation'
  const [episodeDetails, setEpisodeDetails] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    event.stopPropagation();
    if (event.dataTransfer.files && event.dataTransfer.files[0]) {
      setFile(event.dataTransfer.files[0]);
    }
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    event.stopPropagation();
  };

  const handleUpload = async (event) => {
    event.preventDefault();
    if (!file) {
      alert('Veuillez d\'abord sélectionner un fichier audio');
      return;
    }

    setIsLoading(true);
    const formData = new FormData();
    formData.append('audio', file);

    try {
      const response = await fetch('http://localhost:8000/api/transcribe', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Erreur lors de la transcription');
      }

      const data = await response.json();
      setTranscription(data.transcription);
      setCurrentStep('details'); // Passer à l'étape du formulaire après la transcription
    } catch (error) {
      console.error('Erreur:', error);
      alert(`Erreur: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const handleEpisodeDetailsSubmit = (details) => {
    setEpisodeDetails(details);
    setCurrentStep('generation');
    // Ici, plus tard, vous lancerez la génération de contenu
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>PodcastContentMaker</h1>
      </header>
      <main>
        {currentStep === 'upload' && (
          <div
            className="upload-container"
            onDrop={handleDrop}
            onDragOver={handleDragOver}
          >
            <h2>Téléchargez votre fichier audio</h2>
            <input
              type="file"
              accept="audio/*"
              onChange={handleFileChange}
            />
            <div>
              {file && <p>Fichier sélectionné : {file.name}</p>}
              <button
                onClick={handleUpload}
                disabled={!file || isLoading}
              >
                {isLoading ? 'Transcription en cours...' : 'Transcrire'}
              </button>
            </div>
          </div>
        )}

        {isLoading && (
          <p>Transcription en cours...</p>
        )}

        {currentStep === 'details' && (
          <div>
            <div className="transcription-result">
              <h3>Transcription terminée !</h3>
              <div className="transcription-preview">
                <p>{transcription.substring(0, 200)}...</p>
                <button 
                  onClick={() => document.getElementById('full-transcription').scrollIntoView({ behavior: 'smooth' })}
                >
                  Voir la transcription complète
                </button>
              </div>
            </div>

            <EpisodeDetailsForm onSubmit={handleEpisodeDetailsSubmit} />

            <div id="full-transcription" className="full-transcription">
              <h3>Transcription complète :</h3>
              <p>{transcription}</p>
            </div>
          </div>
        )}

        {currentStep === 'generation' && (
          <div>
            <h2>Prêt pour la génération de contenu</h2>
            <div className="episode-summary">
              <h3>Détails de l'épisode :</h3>
              <p><strong>Nom :</strong> {episodeDetails.episodeName}</p>
              <p><strong>Marque :</strong> {episodeDetails.brand}</p>
              {episodeDetails.series && <p><strong>Série :</strong> {episodeDetails.series}</p>}
              {episodeDetails.keywords && <p><strong>Mots-clés :</strong> {episodeDetails.keywords}</p>}

              <button className="generate-button">
                Générer le contenu
              </button>
            </div>

            <div className="transcription-reference">
              <h3>Transcription utilisée :</h3>
              <p>{transcription.substring(0, 200)}...</p>
              <button 
                onClick={() => document.getElementById('generation-transcription').scrollIntoView({ behavior: 'smooth' })}
              >
                Voir la transcription complète
              </button>

              <div id="generation-transcription" className="full-transcription">
                <p>{transcription}</p>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
