import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [transcript, setTranscript] = useState(null);
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];

    if (selectedFile) {
      if (selectedFile.type.includes('audio')) {
        setFile(selectedFile);
        setFileName(selectedFile.name);
        setError('');
      } else {
        setError('Veuillez sélectionner un fichier audio (MP3, WAV, etc.)');
        setFile(null);
        setFileName('');
      }
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();

    const droppedFile = e.dataTransfer.files[0];

    if (droppedFile) {
      if (droppedFile.type.includes('audio')) {
        setFile(droppedFile);
        setFileName(droppedFile.name);
        setError('');
      } else {
        setError('Veuillez déposer un fichier audio (MP3, WAV, etc.)');
      }
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleUpload = async () => {
    if (!file) {
      setError('Veuillez d\'abord sélectionner un fichier audio');
      return;
    }

    const formData = new FormData();
    formData.append('audioFile', file);

    setIsUploading(true);
    setError('');

    try {
      const response = await axios.post('http://localhost:8000/api/transcribe', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setTranscript(response.data);
    } catch (error) {
      console.error('Erreur:', error);
      setError('Une erreur est survenue lors de la transcription');
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Transcription de Podcast</h1>
      </header>

      <main className="App-main">
        <div className="upload-container">
          <div 
            className="drop-area" 
            onDrop={handleDrop} 
            onDragOver={handleDragOver}
            onClick={() => document.getElementById('file-input').click()}
          >
            <input 
              type="file" 
              id="file-input" 
              onChange={handleFileChange} 
              style={{ display: 'none' }} 
              accept="audio/*"
            />

            {fileName ? (
              <div className="file-info">
                <p className="file-name">{fileName}</p>
                <button className="remove-btn" onClick={(e) => {
                  e.stopPropagation();
                  setFile(null);
                  setFileName('');
                }}>
                  Supprimer
                </button>
              </div>
            ) : (
              <div className="upload-message">
                <p>Déposez votre fichier audio ici</p>
                <p className="or-text">ou</p>
                <p>Cliquez pour sélectionner un fichier</p>
              </div>
            )}
          </div>

          {error && <p className="error-message">{error}</p>}

          <button 
            className="transcribe-btn" 
            onClick={handleUpload} 
            disabled={!file || isUploading}
          >
            {isUploading ? 'Transcription en cours...' : 'Transcrire'}
          </button>
        </div>

        {transcript && (
          <div className="transcript-container">
            <h2>Transcription</h2>

            <div className="transcript-content">
              <p className="transcript-duration">Durée: {transcript.duration}</p>

              {transcript.segments.map((segment, index) => (
                <div key={index} className="segment">
                  <div className="segment-header">
                    <span className="speaker">{segment.speaker}</span>
                    <span className="timestamp">{segment.timestamp}</span>
                  </div>
                  <p className="segment-text">{segment.text}</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
